script:
function rnd(n) {
    return Math.floor(Math.random()*n);
}

function rndColors(n) {
    var i = 0,
        a = new Array(n),
        o;
    for(; i < n; i++) {
        o = {
            r: rnd(256),
            g: rnd(256),
            b: rnd(256)
        };
        o.color = "rgb("+o.r+","+o.g+","+o.b+")";
        o.colorC = {type:'color',value:o.color};
        a[i] = o;
    }
    return a;
}

function getChart(theSet, prediction) {
    var chart = {
        x: [],
        y: [],
        info: []
    };
    var result = {
        type: 'chart',
        value: {
            data: [chart]
        }
    };

    var i = 0,
        l = theSet.length;

    for (; i < l; i++) {
        chart.x[i] = prediction[i][0] + prediction[i][2][0];
        chart.y[i] = prediction[i][1] + prediction[i][2][1];
        chart.info[i] = theSet[i]
    }
    return result;
}

var def = API.cache('def');
if (!def) {
    def = API.getData('defaultOptions').resurrect();
    API.cache('def', def);
}

var options = $.extend(true, {}, def, value.resurrect());

var trainingSet = rndColors(options.colors.train);
var testSet = rndColors(options.colors.test);

var som = new ML.nn.SOM(options.som.x, options.som.y, {
    iterations: options.som.it,
    fields: {
        r: [0, 255],
        g: [0, 255],
        b: [0, 255]
    }
});

som.train(trainingSet);

var predTraining = som.predict(true);
var predTest = som.predict(testSet, true);

var model = som.export();

API.createData('somModel', model);
API.createData('somTraining', getChart(trainingSet, predTraining));
API.createData('somTest', getChart(testSet, predTest));

resolve();

schema:
{
    "type": "object",
    "properties": {
        "colors": {
            "type": "object",
            "label": "Color generation",
            "properties": {
                "train": {
                    "type": "number",
                    "label": "Number of training colors",
                    "default": 1000
                },
                "test": {
                    "type": "number",
                    "label": "Number of test colors",
                    "default": 100
                }
            }
        },
        "som": {
            "type": "object",
            "label": "SOM",
            "properties": {
                "x": {
                    "type": "number",
                    "label": "Grid x",
                    "default": 10
                },
                "y": {
                    "type": "number",
                    "label": "Grid y",
                    "default": 10
                },
                "it": {
                    "type": "number",
                    "label": "Iterations",
                    "default": 5
                }
            }
        }
    }
}
